export function TechStackBeam() {
  const containerRef = useRef<HTMLDivElement>(null);
  const div1Ref = useRef<HTMLDivElement>(null); // HTML
  const div2Ref = useRef<HTMLDivElement>(null); // CSS
  const div3Ref = useRef<HTMLDivElement>(null); // JS
  const div4Ref = useRef<HTMLDivElement>(null); // VS Code (Center)
  const div5Ref = useRef<HTMLDivElement>(null); // PHP
  const div6Ref = useRef<HTMLDivElement>(null); // SQL
  const div7Ref = useRef<HTMLDivElement>(null); // Java

  return (
    <div
      className="relative flex h-[400px] w-full items-center justify-center overflow-hidden p-10"
      ref={containerRef}
    >
      <div className="flex size-full flex-col items-stretch justify-between gap-10">
        <div className="flex flex-row items-center justify-between">
          <Circle ref={div1Ref}>
            <Icons.html5 className="size-8" />
          </Circle>
          <Circle ref={div5Ref}>
            <Icons.php className="size-8" />
          </Circle>
        </div>
        <div className="flex flex-row items-center justify-between">
          <Circle ref={div2Ref}>
            <Icons.css3 className="size-8" />
          </Circle>
          <Circle ref={div4Ref} className="size-24 border-indigo-500/50 bg-indigo-50/10 dark:bg-indigo-900/20">
            <Icons.visualstudiocode className="size-12" />
          </Circle>
          <Circle ref={div6Ref}>
            <Icons.mysql className="size-10" />
          </Circle>
        </div>
        <div className="flex flex-row items-center justify-between">
          <Circle ref={div3Ref}>
            <Icons.javascript className="size-8" />
          </Circle>
          <Circle ref={div7Ref}>
            <Icons.java className="size-8" />
          </Circle>
        </div>
      </div>

      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div1Ref}
        toRef={div4Ref}
        curvature={-75}
        endYOffset={-10}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div2Ref}
        toRef={div4Ref}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div3Ref}
        toRef={div4Ref}
        curvature={75}
        endYOffset={10}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div5Ref}
        toRef={div4Ref}
        curvature={-75}
        endYOffset={-10}
        reverse
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div6Ref}
        toRef={div4Ref}
        reverse
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div7Ref}
        toRef={div4Ref}
        curvature={75}
        endYOffset={10}
        reverse
      />
    </div>
  );
}
